Rule.Source:
import gov.dhs.cdm.constants.CustomGlobalStringConst;
import sailpoint.object.Identity;
import sailpoint.object.Link;
import java.util.List;
import org.apache.commons.logging.Log;
import org.apache.commons.logging.LogFactory;
Log log = LogFactory.getLog("rule.IdentityAttribute-Privileged");
log.trace("Entering privileged identity attribute rule");
String finalPrivValue = CustomGlobalStringConst.PRIVILEGED_DEFAULT_VALUE;
if (identity != null) {
  List identityAccounts = identity.getLinks();
  if (identityAccounts != null) {
    int numAccounts = identityAccounts.size();
    int curAccount = 0;
    while (curAccount < numAccounts
        && (finalPrivValue == null || finalPrivValue
            .equals(CustomGlobalStringConst.PRIVILEGED_DEFAULT_VALUE))) {
      if (!(identityAccounts.get(curAccount) instanceof Link)) {
        log.error("Error: identity.getLinks returned non-Link objects. Exiting privileged identity attribute rule");
        break;
      }
      Link account = (Link) identityAccounts.get(curAccount);
      Object privValue =
          account
              .getExtendedAttribute(CustomGlobalStringConst.PRIVILEGED_ACCOUNT_LINK_ATTRIBUTE_NAME);
      log.debug("privilieged attribute value is: " + privValue);
      if (privValue instanceof String) {
        String privValueString = (String) privValue;
        if (privValueString.equals(CustomGlobalStringConst.PRIVILEGED_TRUE_VALUE)) {
          finalPrivValue = CustomGlobalStringConst.PRIVILEGED_TRUE_VALUE;
          log.debug("final privileged value is: " + finalPrivValue);
        }
      }
      curAccount++;
    }
  } else {
    log.warn("Identity accounts are null.");
  }
} else {
  log.warn("Identity is null.");
}
log.trace("Exiting privileged identity attribute rule - Returning: " + finalPrivValue);
return finalPrivValue;
